import React ,{Component} from 'react'
import {ProductConsumer} from '../Context';
import {Link} from 'react-router-dom';
import {ButtonContainer} from './Button';
import styled from 'styled-components';


export default class Details extends Component{

    render(){
        return(
            <ProductConsumer>
                {(value =>{
                    //console.log(value.detailProduct); 
                   const {id,company,img,info,price,title,inCart} = value.detailProduct;
                   return(
                       <div className="container py-5">
                           {/* Title */}
                           <div className="row">
                                <div className="col-10 mx-auto text-center text-slanted text-blue  py-5">
                                    <h1>{title}</h1>
                                </div>
                           </div>
                           {/* End Title */}
                           {/* Product Info */}
                           <div className="row">
                                {/* Product Image */}
                                <div className="col-10 mx-auto col-m-6 my-3 ">
                                    <img src={img} className="img-fluid" alt="product "/>
                                </div>
                                {/* Product Text */}
                                <div className="col-10 mx-auto col-m-6 my-3 text-capitalize">
                                    <h2>model: {title}</h2>
                                    <h4 className="text-title text-uppercase text-muted mt-3 mb-2">
                                        made by :<span className="text-uppercase">{company}</span>
                                    </h4>
                                    <h4  className="text-blue">
                                        <strong>
                                            price: <span>$</span>{price}
                                        </strong>
                                    </h4>
                                    <p className="text-capitalize font-weight-bold mt-3 mb-0 ">
                                        some info about product item
                                    </p>
                                    <p className="text-muted lead">
                                        {info}
                                    </p>
                                    {/* MAKING BUTTONS */}
                                    <div>
                                        <Link to="/">
                                            <ButtonContainer>
                                                Back To Products
                                            </ButtonContainer>
                                        </Link>
                                        <ButtonContainer CART disabled={ inCart?true : false} onClick={()=>{
                                            value.addToCart(id);
                                            value.openModal(id);
                                            }}>
                                                {inCart?"inCart" : "add to cart"}
                                        </ButtonContainer>
                                    </div>
                                </div>
                                <div>

                                    <div class="col-lg-6 col-md-6">
                                    <form class="p-5 grey-text">
                                        <div class="md-form form-lg"> <i class="fas fa-user prefix"></i>
                                        <input type="text" id="form3" size="600" class="form-control form-control-sm"/>
                                        <label for="form3">Your name</label>
                                        </div>
                                        <div class="md-form form-sm"> <i class="fas fa-envelope prefix"></i>
                                        <input type="text" id="form2" class="form-control form-control-sm"/>
                                        <label for="form2">Your email</label>
                                        </div>
                                        <div class="md-form form-sm"> <i class="fas fa-route prefix"></i>
                                        <input type="text" id="form14" class="form-control form-control-sm"/>
                                        <label for="form2">Your address line</label>
                                        </div>
                                        <div class="md-form form-sm"> <i class="fas fa-tag prefix"></i>
                                        <input type="text" id="form32" class="form-control form-control-sm"/>
                                        <label for="form34">Subject</label>
                                        </div>
                                        <div class="md-form form-sm"> <i class="fas fa-pencil-alt prefix"></i>
                                        <textarea type="text" id="form8" class="md-textarea form-control form-control-sm" rows="4"></textarea>
                                        <label for="form8">Your message</label>
                                        </div>
                                     
                                        <div class="text-center mt-4">
                                        <button class="btn btn-primary">Ship Now <i class="far fa-paper-planeml-1"></i></button>
                                        </div>
                                    </form>
                                    </div>

                                </div>
                           </div>
                       </div>
                   )
                })}
            </ProductConsumer>
        )
    }
}
const SelectContainer = styled.div `


`;
