import React ,{Component} from 'react'
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import {ProductConsumer} from '../Context';
import PropTypes from 'prop-types';

export default class Product extends Component{

    render(){
        const {id, title, img, price, inCart} = this.props.product;
        return(
          <ProductWrapper className="col-9 mx-auto col-md-6 col-lg-3 my-3">
              <div className="card">
                    <div className="img-container p-5" onClick={() => console.log("You clicked on 'Image Container'!!")}>
                        <Link to="/details">
                            <img src={img} alt="product" className="card-img-top"></img>
                        </Link>
                        <button className="cart-btn" disabled={inCart?true:false} onClick={()=> console.log("added to the cart")}>
                            {inCart?(<p className="text-capitalize mb-0" disabled>in cart</p>):(<i className="fas fa-cart-plus"/>)}
                        </button>
                    </div>

                  {/*Here i am puting names of products*/}

                  <div className="card-footer d-flex justify-content-between">
                     <p className="align-self-center mb-0">
                         {title}
                     </p>
                     <h5 className="text-blue font-italic mb-0">
                        <span className="mr-1">$</span>
                        {price}
                     </h5> 
                  </div>
              </div>
                 
          </ProductWrapper> 
        )
    }
}

{/*Below we are validating making sure u dont disturb JSON data types*/}

Product.propTypes = {
    product : PropTypes.shape({
        id:PropTypes.number,
        img:PropTypes.string,
        title:PropTypes.string,
        price:PropTypes.number,
        inCart:PropTypes.bool
    }).isRequired
}
