import React, {Component} from 'react';
import {storeProducts,detailProduct} from './data';

const ProductContext = React.createContext();
//Provider
//Consumer
 class ProductProvider extends Component{
     state = {
         product: [],
         detailProduct: detailProduct,
         cart:[]
     };
     componentDidMount(){
         this.setProducts();
     }
     setProducts = () =>{
        let tempProduct = [];
        storeProducts.forEach(item =>{
            const singleItem = {...item};
            tempProduct = [...tempProduct,singleItem];
        })
        this.setState(() =>{
             return {product : tempProduct}
        });
     };
     getItem = id =>{
         const product = this.state.product.find(item => item.id === id);//later to change 
         return product;
     }
     //Handling of Details
     handleDetail = id =>{
        const product = this.getItem(id);
        this.setState(()=>{ 
            return {detailProduct:product }
        })
     };
     //Handling of Cart
     addToCart = (id) =>{
        let tempCartProduct = [...this.state.product];
        const index = tempCartProduct.indexOf(this.getItem(id));
        const cartProduct = tempCartProduct[index];
        cartProduct.inCart = true;
        cartProduct.count = 1;
        const price = cartProduct.price;
        cartProduct.total = price;
        this.setState(()=>{
            return {products:tempCartProduct , cart:[...this.state.cart , cartProduct]}
        } ,()=>{
            console.log(this.state);  
        });
    };
    render() {
        return(
            <ProductContext.Provider value={{
                ...this.state,
                handleDetail:this.handleDetail,
                addToCart:this.addToCart
            }}>
                {this.props.children}
            </ProductContext.Provider>
        )
    }
}

const ProductConsumer = ProductContext.Consumer;
export {ProductProvider,ProductConsumer};
