import React ,{Component} from 'react'
import {Link} from 'react-router-dom';
import logo from '../images/tShirt-shopping.png';
import {ButtonContainer} from './Button';
import styled from 'styled-components';
import data from '../data';
import Product from './Product';



export default class Navbar extends Component{
    
    state = {
        search : ''
    }
    renderProduct = product => {

        const {search} = this.state;
        var product = product.title.toLowerCase();

        if(search !== "" && product.title.toLowerCase().indexOf(search.toLowerCase()) === -1 ){
            return null;
        }
        else{
            const {id, title, img, price, inCart} = this.props.product;

        <ProductWrapper className="col-9 mx-auto col-md-6 col-lg-3 my-3">
        <div className="card">
            <ProductConsumer>

                {(value) =>(<div className="img-container p-5" onClick={() => value.handleDetail(id)}>
                  <Link to="/details">
                      <img src={img} alt="product" className="card-img-top"></img>
                  </Link>
                  <button className="cart-btn" disabled={inCart?true:false} onClick={()=> {
                      value.addToCart(id);
                      value.openModal(id);
                  }}>
                      {inCart?(<p className="text-capitalize mb-0" disabled>in cart</p>):(<i className="fas fa-cart-plus"/>)}
                  </button>
              </div>)}
              
            </ProductConsumer>
            {/*Here i am puting names of products*/}

            <div className="card-footer d-flex justify-content-between">
               <p className="align-self-center mb-0">
                   {title}
               </p>
               <h5 className="text-blue font-italic mb-0">
                  <span className="mr-1">$</span>
                  {price}
               </h5> 
            </div>
            
        </div>
    </ProductWrapper> 
        }

    }
    onchange = e => {
        this.setState({search : e.target.value});
    }


    render(){
        return(
          <NavWrapper  className="navbar navbar-expand-sm  navbar-dark px-sm-5 ">

                {/* 
                https://www.iconfinder.com/icons/1243689/call_phone_icon
                Creative Commons (Attribution 3.0 Unported);
                https://www.iconfinder.com/Makoto_msk */}

              <Link to="/" >
                  <img src={logo} alt="store" className="navbar-brand"/>
              </Link>

              <ul className="navbar-nav align-items-center">
                  <Link to="/" className="nav-link">
                        Product
                  </Link>
              </ul>
              <Link to="/cart" className="ml-auto">
                  <ButtonContainer>
                    <span className="mr-2"><i className="fas fa-cart-plus"></i></span>
                    my cart
                  </ButtonContainer>

              </Link>
              <div className="searchSection">
                <input type="text" icon="search" onChange={this.onchange}/>
              </div>
              
          </NavWrapper>

        )
    }
}
const NavWrapper = styled.nav `
     background: var(--mainBlue);
     .nav-link{
        color: var(--mainWhite) !important;
        font-size:1.3rem;
        text-transform: capitalize !important;
     }
`;
